import React from "react";
import Watchlist from "../../buttons/Watchlist";
import { Swiper, SwiperSlide } from "swiper/react";
import WatchNow from "../../buttons/WatchNow";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";
import "./styles.css";
import { Pagination, Navigation } from "swiper/modules";
import styled from "styled-components";

const Title = styled.p`
  position: absolute;
  left: 0;
  top: 0;
  z-index: 11;
  margin-top: 40px;
  margin-left: 40px;
  font-family: Lato;
  font-size: 48px;
  font-weight: 800;
  line-height: 48px;
  color: #fff;
`;

const Column = styled.div`
  display: flex;
  flex-direction: column;
`;

const Swipe = () => {
  return (
    <Column>
      <Swiper
        slidesPerView={1}
        spaceBetween={30}
        loop={true}
        pagination={{ clickable: true }}
        navigation={true}
        modules={[Pagination, Navigation]}
        className="mySwiper"
      >
        <SwiperSlide>
          <img src="/images/CR.jfif" alt="2 man and 1 women"></img>
          <Title>The Crown</Title>
        </SwiperSlide>
        <SwiperSlide>
          <img
            src="/images/loki.jfif"
            alt="loki and other main characters"
          ></img>
          <Title>Loki</Title>
        </SwiperSlide>
        <SwiperSlide>
          <img
            src="/images/chernobyl.png"
            alt="people with chemical suits"
          ></img>
          <Title>Chernobyl</Title>
        </SwiperSlide>
        <WatchNow />
        <Watchlist />
      </Swiper>
    </Column>
  );
};

export default Swipe;
