import React, { useEffect, useState } from "react";
import axios from "axios";
import sliceText from "../../functions/sliceText";
import styled from "styled-components";
import Rating from "../../functions/Rating";
const baseUrl = "https://www.omdbapi.com/";
const apiKey = "?apikey=8eac0ca3";

const Div = styled.div`
  position: relative;
  width: 295px;
  height: 417px;
`;

const H3 = styled.h3`
  margin-top: 15px;
  font-family: Lato;
  font-size: 29.24px;
  font-weight: 600;
  line-height: 35.09px;
`;
const MovieCard = ({ imdbID }) => {
  const [movieDetails, setMovieDetails] = useState(null);
  useEffect(() => {
    const getMovieDetails = async () => {
      try {
        const response = await axios.get(`${baseUrl}${apiKey}&i=${imdbID}`);
        setMovieDetails(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    getMovieDetails();
  }, [imdbID]);

  if (!movieDetails) return <p>Loading...</p>;

  return (
    <Div>
      <img
        style={{ width: "295px", height: "417px" }}
        src={movieDetails.Poster}
        alt={movieDetails.Title}
      />
      <H3>{movieDetails.Title}</H3>
      <p>{sliceText(movieDetails.Plot, 110)}</p>
      <p>{Rating(8)}</p>
    </Div>
  );
};

export default MovieCard;
