import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";

import { useSelector } from "react-redux";

const HeaderWrapper = styled.header`
  display: flex;
  gap: 194px;
  align-items: center;
  margin-top: 61px;
`;

const Logo = styled.div`
  display: flex;
`;

const LogoText = styled.h1`
  font-family: "Luckiest Guy", cursive;
  width: 117px;
  display: flex;
  flex-direction: column;
  font-size: 32px;
  font-weight: 400;
  line-height: 32px;
  color: #f8b319;
`;

const NavList = styled.nav`
  display: flex;
  align-items: center;
`;

const Ul = styled.ul`
  display: flex;
  gap: 50px;
`;

const ListItem = styled.li`
  list-style: none;
`;

const StyledLink = styled(Link)`
  font-family: "Lato", sans-serif;
  font-weight: 600;
  font-size: 24px;
  line-height: 24px;
  text-decoration: none;
  color: ${({ theme }) => (theme.isDarkMode ? "#fff" : "black")};
`;

const NavBar = styled.div`
  display: flex;
  gap: 100px;
`;

const SearchBar = styled.input`
  background-color: #21242d;
  width: 315px;
  height: 56px;
  color: #fff;
  color: ${({ theme }) => (theme.isDarkMode ? "#fff" : "black")};
  background-color: ${({ theme }) => (theme.isDarkMode ? "#21242D" : "#fff")};
`;

const Header = () => {
  const isDarkMode = useSelector((state) => state.theme.isDarkMode);
  return (
    <HeaderWrapper>
      <Logo>
        <img style={{ width: 59 }} src="/images/logo.png" alt="Logo" />
        <LogoText>Honey Movies</LogoText>
      </Logo>
      <NavBar>
        <NavList>
          <Ul>
            <ListItem>
              <StyledLink style={{ color: "#F8B319" }} to="/movies">
                Movies
              </StyledLink>
            </ListItem>
            <ListItem>
              <StyledLink to="/tv-shows">TV Shows</StyledLink>
            </ListItem>
            <ListItem>
              <StyledLink to="/anime">Anime</StyledLink>
            </ListItem>
          </Ul>
        </NavList>
        <SearchBar />
        <DarkMode />
      </NavBar>
    </HeaderWrapper>
  );
};

export default Header;
