import axios from "axios";
import textSplicer from "./textSplicer";
import React, { useEffect, useState } from "react";

const Products = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("https://fakestoreapi.com/products");
        setProducts(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, []);

  return (
    <div style={{ display: "flex", flexWrap: "wrap", gap: "30px" }}>
      {products.map((product) => (
        <div
          style={{
            display: "flex",
            width: "400px", // Ширина родительского элемента
            marginBottom: "30px", // Пространство между продуктами
          }}
          key={product.id}
        >
          <img
            style={{ width: "200px", height: "auto" }} // Фиксированная ширина изображения
            src={product.image}
            alt={product.title}
          />
          <div style={{ marginLeft: "20px" }}>
            {" "}
            {/* Пространство между изображением и описанием */}
            <p>{textSplicer(product.title, 55)}</p>
            <p>{textSplicer(product.description, 111)}</p>
            <span>Price: {product.price}$</span>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Products;
