import { createSlice } from "@reduxjs/toolkit";

const themeSlice = createSlice({
  name: "theme",
  initialState: {
    isDarkMode: true,
  },
  reducers: {
    enabledDarkMode: (state) => {
      state.isDarkMode = true;
    },
    disabledDarkMode: (state) => {
      state.isDarkMode = false;
    },
  },
});

export const { enabledDarkMode, disabledDarkMode } = themeSlice.actions;
export default themeSlice.reducer;
