import React, { useEffect, useState } from "react";
import axios from "axios";

const baseUrl = "https://www.omdbapi.com/";
const apiKey = "?apikey=8eac0ca3";

const MovieCard = ({ imdbID }) => {
  const [movieDetals, setMovieDetails] = useState(null);
  useEffect(() => {
    const getMovieDetails = async () => {
      try {
        const response = await axios.get(`${baseUrl}${apiKey}&i=${imdbID}`);
        setMovieDetails(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    getMovieDetails();
  }, [imdbID]);
  return (
    <div>
      {movieDetals.map((item) => (
        <div key={item.id}>
          <img src={item.Poster} />
          <p>{item.Title}</p>
          <p>{item.Plot}</p>
          <p>{item.imdbRating}</p>
        </div>
      ))}
    </div>
  );
};

export default MovieCard;
