import React from "react";
import styled from "styled-components";

import StarBorderRoundedIcon from "@mui/icons-material/StarBorderRounded";
import StarHalfRoundedIcon from "@mui/icons-material/StarHalfRounded";
import StarRoundedIcon from "@mui/icons-material/StarRounded";

const OrangeStar = styled(StarRoundedIcon)`
  color: #f8b319;
`;

const OrangeStarHalf = styled(StarHalfRoundedIcon)`
  color: #f8b319;
`;

const OrangeStarBorder = styled(StarBorderRoundedIcon)`
  color: #f8b319;
`;

const Star = styled.span`
  font-size: ${({ size }) => size}px;
  color: gold;
`;

const Rating = ({ imdbRating, size }) => {
  const rating = parseFloat(imdbRating) / 2;
  const fullStars = Math.floor(rating);
  const hasHalfStar = rating % 1 >= 0.5;
  const emptyStars = 5 - fullStars - (hasHalfStar ? 1 : 0);

  return (
    <div>
      {Array.from({ length: fullStars }, (_, index) => (
        <OrangeStar style={{ fontSize: "21px" }} key={`full-${index}`} />
      ))}
      {hasHalfStar && <OrangeStarHalf style={{ fontSize: "21px" }} />}
      {Array.from({ length: emptyStars }, (_, index) => (
        <OrangeStarBorder style={{ fontSize: "21px" }} key={`empty-${index}`} />
      ))}
      <Star size={size} key={`empty-${index}`}>
        â˜†
      </Star>
    </div>
  );
};

export default Rating;
