import React from "react";
import "./App.css";
import { ThemeProvider } from "styled-components";
import { useSelector } from "react-redux";
import { createGlobalStyle } from "styled-components";
import Routing from "./Routing/Routing";

const GlobalStyle = createGlobalStyle`
  body {
    background-color: ${({ theme }) => (theme.isDarkMode ? "black" : "#fff")};
    color: ${({ theme }) => (theme.isDarkMode ? "#fff" : "black")};
    transition: all 0.2s ease;

  }
`;

const DeffaultStyles = styled.div`
  @import url("https://fonts.googleapis.com/css2?family=Luckiest+Guy&display=swap");
  @import url("https://fonts.googleapis.com/css2?family=Lato:ital,wght@0,100;0,300;0,400;0,700;0,900;1,100;1,300;1,400;1,700;1,900&family=Luckiest+Guy&display=swap");
  margin: 0;
  padding: 0;
  box-sizing: border-box;
`;

const App = () => {
  const isDarkMode = useSelector((state) => state.theme.isDarkMode);

  return (
    <ThemeProvider theme={{ isDarkMode }}>
      <GlobalStyle />
      <Routing />
    </ThemeProvider>
  );
};

export default App;
