import React, { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components";
import MovieCard from "./MovieCard";
import { Link } from "react-router-dom";

const Section = styled.section`
  margin-top: 210px;
`;

const Wrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 34px;
  width: 999px;
`;

const H2 = styled.h2`
  font-family: Lato;
  font-size: 48px;
  font-weight: 800;
  line-height: 48px;
  margin-bottom: 66px;
`;

const Card = styled(Link)`
  color: ${({ theme }) => (theme.isDarkMode ? "#fff" : "black")};
  text-decoration: none;
  margin-top: 220px; // Это добавляет отступ ко всем карточкам

  &:nth-child(-n + 3) {
    margin-top: 0;
  }
`;

const AddMore = styled.button`
  background-color: #f8b319;
  padding: 5px 10px;
  border-radius: 5px;
  height: 100%;
  margin: 200px 45%;
  cursor: pointer;
`;

const baseUrl = "https://www.omdbapi.com/";
const apiKey = "?apikey=8eac0ca3";
const Url = `${baseUrl}${apiKey}&s=movie`;

const MainTopRated = () => {
  const [movies, setMovies] = useState([]);
  const [movieQuantity, setMovieQuantity] = useState(3);
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(Url);
        setMovies(response.data.Search);
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, []);

  const addMovie = () => {
    setMovieQuantity((prevCounter) => prevCounter + 3);
  };

  return (
    <Section>
      <H2>Top Rated Movies</H2>
      <Wrapper>
        {movies.slice(0, movieQuantity).map((movie) => (
          <Card to={`/about-movie/${movie.imdbID}`}>
            <MovieCard key={movie.imdbID} imdbID={movie.imdbID} />
          </Card>
        ))}
        <AddMore onClick={addMovie}>Add More</AddMore>
      </Wrapper>
    </Section>
  );
};

export default MainTopRated;
