import React, { useState } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import Search from "./Search";

const NavList = styled.nav`
  display: flex;
  align-items: center;
`;

const Ul = styled.ul`
  display: flex;
  gap: 50px;
`;

const ListItem = styled.li`
  list-style: none;
`;

const StyledLink = styled(Link)`
  font-family: "Lato", sans-serif;
  font-weight: 600;
  font-size: 24px;
  line-height: 24px;
  text-decoration: none;
  color: ${({ theme }) => (theme.isDarkMode ? "#fff" : "black")};
`;

const NavBar = styled.div`
  display: flex;
  gap: 100px;
`;

const SearchWrapper = styled.div`
  position: relative;
`;

const SearchBar = styled.input`
  background-color: #21242d;
  width: 315px;
  height: 56px;
  border-radius: 12px;
  border: 1px solid rgba(249, 249, 249, 0.1);
  color: #fff;
  color: ${({ theme }) => (theme.isDarkMode ? "#fff" : "black")};
  background-color: ${({ theme }) => (theme.isDarkMode ? "#21242D" : "#fff")};
  filter: ${({ theme }) => (theme.isDarkMode ? "invert(0)" : "invert(1)")};
  padding-left: 64px;
`;

const SearchIcon = styled.img`
  position: absolute;
  left: 25px;
  top: 16px;
`;

const KeyIcon = styled.img`
  position: absolute;
  left: 270px;
  top: 16px;
`;

const NavRight = () => {
  const [searchInput, setSearchInput] = useState(null);
  const userInput = (e) => {
    setSearchInput(e.target.value);
  };
  return (
    <NavBar>
      {console.log(ListItem)}
      <NavList>
        <Ul>
          <ListItem>
            <StyledLink style={{ color: "#F8B319" }} to="/movies">
              Movies
            </StyledLink>
          </ListItem>
          <ListItem>
            <StyledLink to="/tv-shows">TV Shows</StyledLink>
          </ListItem>
          <ListItem>
            <StyledLink to="/anime">Anime</StyledLink>
          </ListItem>
        </Ul>
      </NavList>
      <SearchWrapper>
        <SearchBar
          onChange={userInput}
          value={searchInput}
          placeholder="Search"
        />
        <SearchIcon src="/images/Search.png" />
        <KeyIcon src="/images/Key.png" />
        <Search movieTitle={searchInput} />
      </SearchWrapper>
    </NavBar>
  );
};

export default NavRight;
