import React, { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components";
import sliceText from "../../functions/sliceText"; // Убедитесь, что путь правильный

const baseUrl = "https://www.omdbapi.com/";
const apiKey = "?apikey=8eac0ca3";

const Div = styled.div`
  position: relative;
  width: 295px;
  height: 417px;
`;

const H3 = styled.h3`
  margin-top: 15px;
  font-family: Lato;
  font-size: 29.24px;
  font-weight: 600;
  line-height: 35.09px;
`;

const CornerDiv = styled.div`
  width: 77px;
  height: 38px;
  background-color: #f8b319;
  border-radius: 0px 17px;
  position: absolute;
  top: 0;
  right: 0;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Image = styled.img`
  width: 295px;
  height: 417px;
  border-radius: 17px;
`;

const RateInCorner = styled.span`
  font-family: Lato;
  font-size: 23px;
  font-weight: 600;
  line-height: 27.6px;
  color: black;
`;

const Release = styled.span`
  font-family: Lato;
  font-size: 17px;
  font-weight: 700;
  line-height: 20.4px;
  margin-top: 11px;
  color: rgba(249, 249, 249, 0.7);
`;

const Description = styled.p`
  font-family: Lato;
  font-size: 14px;
  font-weight: 600;
  line-height: 16.8px;
  margin-top: 15px;
`;

const MovieCard = ({ imdbID }) => {
  const [movieDetails, setMovieDetails] = useState(null);

  useEffect(() => {
    const getMovieDetails = async () => {
      try {
        const response = await axios.get(
          `${baseUrl}${apiKey}&i=${imdbID}&plot=full`
        );
        console.log(response.data); // Логируем данные
        setMovieDetails(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    getMovieDetails();
  }, [imdbID]);

  if (!movieDetails) return <p>Loading...</p>;

  return (
    <Div>
      <Image
        src={movieDetails.Poster || "default_image_url.jpg"}
        alt={movieDetails.Title || "No Title"}
      />
      <H3>{movieDetails.Title || "No Title Available"}</H3>
      <Release>{movieDetails.Released || "Release date not available"}</Release>
      <Description>
        {movieDetails.Plot
          ? sliceText(movieDetails.Plot, 110)
          : "No description available"}
      </Description>
      <CornerDiv>
        <RateInCorner>{movieDetails.imdbRating || "N/A"}</RateInCorner>
      </CornerDiv>
    </Div>
  );
};

export default MovieCard;
