import React from "react";
import { enableDarkMode, disableDarkMode } from "../features/slices/themeSlice";
import { useDispatch, useSelector } from "react-redux";
import LightModeIcon from "@mui/icons-material/LightMode";
import styled from "styled-components";

const StyledIcon = styled(LightModeIcon)(({ theme }) => ({
  backgroundColor: "transparent", // Убираем фон
  border: "none", // Убираем бордер, если он есть
  padding: 0, // Убираем паддинг, если он есть
}));

const DarkMode = () => {
  const isDarkMode = useSelector((state) => state.theme.isDarkMode);
  const dispatch = useDispatch();

  const toggleDarkMode = () => {
    dispatch(isDarkMode ? disableDarkMode() : enableDarkMode());
  };

  return (
    <button onClick={toggleDarkMode}>
      {isDarkMode ? (
        <StyledIcon />
      ) : (
        <img style={{ width: "20px" }} src="/images/darkMode.png"></img>
      )}
    </button>
  );
};

export default DarkMode;
