import React, { useEffect, useState } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import "./TopRated.css";
import { Pagination } from "swiper/modules";
import axios from "axios";
import styled from "styled-components";
import Rating from "../components/functions/Rating";
const H3 = styled.h3`
  margin-top: 15px;
  font-family: Lato;
  font-size: 29.24px;
  font-weight: 600;
  line-height: 35.09px;
`;

const CornerDiv = styled.div`
  width: 77px;
  height: 38px;
  background-color: #f8b319;
  border-radius: 0px 17px;
  position: absolute;
  top: 0;
  right: 0;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Image = styled.img`
  width: 295px;
  height: 417px;
  border-radius: 17px;
`;

const RateInCorner = styled.span`
  font-family: Lato;
  font-size: 23px;
  font-weight: 600;
  line-height: 27.6px;
  color: black;
`;

const Release = styled.span`
  font-family: Lato;
  font-size: 17px;
  font-weight: 700;
  line-height: 20.4px;
  margin-top: 11px;
  color: rgba(249, 249, 249, 0.7);
`;

const Description = styled.p`
  font-family: Lato;
  font-size: 14px;
  font-weight: 600;
  line-height: 16.8px;
  margin-top: 15px;
`;

const TopRated = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          "https://www.omdbapi.com/?apikey=8eac0ca3&s=movie"
        );
        if (response.data && response.data.Search) {
          const movieDetailsPromises = response.data.Search.map(
            async (movie) => {
              const detailResponse = await axios.get(
                `https://www.omdbapi.com/?apikey=8eac0ca3&i=${movie.imdbID}`
              );
              return detailResponse.data;
            }
          );

          const moviesWithDetails = await Promise.all(movieDetailsPromises);
          setMovies(moviesWithDetails);
        }
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, []);

  if (!movies.length) {
    return <h2>Loading...</h2>;
  }

  return (
    <>
      <Swiper
        slidesPerView={4}
        spaceBetween={30}
        pagination={{
          clickable: true,
        }}
        modules={[Pagination]}
        className="mySwiper"
      >
        {movies.map((movie) => (
          <SwiperSlide key={movie.imdbID}>
            <Image src={movie.Poster} alt={movie.Title} />
            <H3>{movie.Title}</H3>
            <Rating imdbRating={movie.imdbRating} starSize="21px">
              {movie.imdbRating}
            </Rating>
            <Release>{movie.Released}</Release>
            <Description>{movie.Plot}</Description>
          </SwiperSlide>
        ))}
      </Swiper>
    </>
  );
};

export default TopRated;
