import React, { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components";

const Section = styled.section`
  margin-top: 210px;
`;
const Wrapper = styled.div`
  display: flex;
  gap: 34px;
`;

const baseUrl = "https://www.omdbapi.com/";
const apiKey = "?apikey=8eac0ca3";
const page = 1;
const Url = `${baseUrl}${apiKey}&s=movie&page=${page}`;

const MoviePlotById = (ID) => {
  const MovieUrlById = `${baseUrl}${apiKey}&i${ID}`;
  const [moviesData, setMoviesData] = useState([]);
  useEffect(() => {
    const fetchMovieById = async () => {
      try {
        const response = await axios.get(MovieUrlById);
        setMoviesData(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchMovieById();
  }, []);
};

const MainTopRated = () => {
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(Url);
        setMovies(response.data.Search);
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, []);
  const [movies, setMovies] = useState([]);
  return (
    <Section>
      <h2>Top Rated Movies</h2>
      <Wrapper>
        {movies.slice(0, 3).map((movie) => (
          <div style={{ listStyle: "none" }} key={movie.imdbID}>
            <img src={movie.Poster}></img>
            <p style={{ position: "absolute" }}>{movie.Title}</p>
          </div>
        ))}
      </Wrapper>
    </Section>
  );
};

export default MainTopRated;
