import React, { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components";

const Section = styled.section`
  margin-top: 210px;
`;

const Wrapper = styled.div`
  display: flex;
  gap: 34px;
`;

const baseUrl = "https://www.omdbapi.com/";
const apiKey = "?apikey=8eac0ca3";
const Url = `${baseUrl}${apiKey}&s=movie&page=1`;

const MovieCard = ({ imdbID }) => {
  const [movieDetails, setMovieDetails] = useState(null);

  useEffect(() => {
    const fetchMovieDetails = async () => {
      try {
        const response = await axios.get(`${baseUrl}${apiKey}&i=${imdbID}`);
        setMovieDetails(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchMovieDetails();
  }, [imdbID]);

  if (!movieDetails) return null;

  return (
    <div style={{ listStyle: "none", position: "relative" }}>
      <img src={movieDetails.Poster} alt={movieDetails.Title} />
      <p style={{ position: "absolute" }}>{movieDetails.Title}</p>
      <p>{movieDetails.Plot}</p>
      <p>Rating: {movieDetails.imdbRating}</p>
    </div>
  );
};

const MainTopRated = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(Url);
        setMovies(response.data.Search);
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, []);

  return (
    <Section>
      <h2>Top Rated Movies</h2>
      <Wrapper>
        {movies.slice(0, 3).map((movie) => (
          <MovieCard key={movie.imdbID} imdbID={movie.imdbID} />
        ))}
      </Wrapper>
    </Section>
  );
};

export default MainTopRated;
