import { createSlice } from "@reduxjs/toolkit";

const themeSlice = createSlice({
  name: "theme",
  initialState: {
    isDarkMode: true,
  },
  reducers: {
    enableDarkMode: (state) => {
      state.isDarkMode = true;
    },
    disableDarkMode: (state) => {
      state.isDarkMode = false;
    },
  },
});
