import React, { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components";
import AddMoreMovies from "./AddMore";

const Section = styled.section`
  margin-top: 210px;
`;

const Wrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 34px;
  width: 999px;
`;

const H2 = styled.h2`
  font-family: Lato;
  font-size: 48px;
  font-weight: 800;
  line-height: 48px;
  margin-bottom: 66px;
`;

const Image = styled.img`
  position: absolute;
  top: 140px;
  left: 210px;
  z-index: -1;
  filter: ${({ theme }) => (theme.isDarkMode ? "invert(0)" : "invert(1)")};
`;

const baseUrl = "https://www.omdbapi.com/";
const apiKey = "?apikey=8eac0ca3";
const Url = `${baseUrl}${apiKey}&s=movie`;

const MainTopRated = () => {
  const [movies, setMovies] = useState([]);
  const [filteredMovies, setFilteredMovies] = useState([]);
  const [movieQuantity, setMovieQuantity] = useState(3);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(Url);
        setMovies(response.data.Search);
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, []);

  useEffect(() => {
    const filtered = movies.filter(() => movies.imdbRating >= 4);
    setFilteredMovies(filtered);
  }, []);

  return (
    <Section>
      <H2>Top Rated Movies</H2>
      <Image src="/images/bg.png" />
      <Wrapper>
        <AddMoreMovies
          filteredMovies={filteredMovies}
          movieQuantity={movieQuantity}
          setMovieQuantity={setMovieQuantity}
        />
      </Wrapper>
    </Section>
  );
};

export default MainTopRated;
