import React, { useEffect, useState } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import { Pagination } from "swiper/modules";
import axios from "axios";
import styled from "styled-components";
import MovieCard from "../components/Main/MainTopRated/MovieCard";
import BackHomeButton from "../components/buttons/BackHomeButton";
const StyledSwiper = styled(Swiper)`
  width: 100%;
  height: 688px;
`;

const StyledSwiperSlide = styled(SwiperSlide)`
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  height: auto;
  background: ${({ theme }) => (theme.isDarkMode ? "black" : "#fff")};
`;

const MainDiv = styled.div``;

const SwiperWrapper = styled.div``;

const H2 = styled.h2`
  font-family: Lato;
  font-size: 48px;
  font-weight: 800;
  line-height: 48px;
  margin-bottom: 66px;
  margin-left: 58px;
`;

const HomeBtn = styled(BackHomeButton)`
  left: 0;
  bottom: 0;
  margin: 0;
  padding: 0;
`;

const baseUrl = "https://www.omdbapi.com/";
const apiKey = "?apikey=8eac0ca3";
const Url = `${baseUrl}${apiKey}&s=movie`;

const TopRated = () => {
  const [movies, setMovies] = useState([]);
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(Url);
        setMovies(response.data.Search);
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, []);

  if (!movies.length) {
    return <h2>Loading...</h2>;
  }

  return (
    <MainDiv>
      <HomeBtn />
      <H2>Top Rate Movies</H2>
      <SwiperWrapper>
        <StyledSwiper
          slidesPerView={4}
          spaceBetween={30}
          pagination={{ clickable: true }}
          modules={[Pagination]}
        >
          {movies.map((movie) => (
            <StyledSwiperSlide key={movie.imdbID}>
              <MovieCard imdbID={movie.imdbID} />
            </StyledSwiperSlide>
          ))}
        </StyledSwiper>
      </SwiperWrapper>
    </MainDiv>
  );
};

export default TopRated;
