import React, { useEffect, useState } from "react";
import styled from "styled-components";
import axios from "axios";
import { useParams } from "react-router-dom";
import DarkMode from "../../components/DarkMode";
import { Link } from "react-router-dom";
import Rating from "../../components/functions/Rating";
const Div = styled.div`
  position: relative;
`;
const BackHome = styled.p`
  font-family: Lato;
  font-size: 28px;
  font-weight: 800;
  line-height: 28px;
`;

const BackHomeWrapper = styled(Link)`
  display: flex;
  gap: 21px;
  align-items: center;
  text-decoration: none;
  color: ${({ theme }) => (theme.isDarkMode ? "#fff" : "black")};
  position: absolute;
  left: 50px;
  top: 172px;
`;

const Image = styled.img``;
const Btn = styled.button`
  background-color: rgba(249, 249, 249, 0.2);
  filter: ${({ theme }) => (theme.isDarkMode ? "invert(0)" : "invert(1)")};
  border: none;
  width: 50px;
  height: 50px;
  border-radius: 15px;
`;

const MovieCard = styled.div`
  display: flex;
  gap: 105px;
  margin: 281px 124px 0px 95px;
`;

const MovieDetails = styled.div``;

const H2 = styled.h2`
  font-family: Lato;
  font-size: 68px;
  font-weight: 800;
  line-height: 81.6px;
`;

const Views = styled.span`
  font-family: Lato;
  font-size: 25px;
  font-weight: 600;
  line-height: 30px;
`;

const IMDb = styled.img`
  width: 57px;
  height: 30px;
`;

const Description = styled.p`
  font-family: Lato;
  font-size: 30px;
  font-weight: 600;
  line-height: 36px;
`;

const baseUrl = "https://www.omdbapi.com/";
const apiKey = "?apikey=8eac0ca3";

const SelectedMovie = () => {
  const { movieID } = useParams();
  const [movie, setMovie] = useState(null);
  useEffect(() => {
    const fetchMovie = async () => {
      try {
        const response = await axios.get(
          `${baseUrl}${apiKey}&i=${movieID}&plot=full`
        );
        if (response.data) {
          setMovie(response.data);
        }
      } catch (error) {
        console.error(error);
      }
    };
    fetchMovie();
  }, [movieID]);

  if (!movie) return <div>Loading...</div>;

  return (
    <Div>
      <BackHomeWrapper>
        <Btn>
          <img src="/images/Previous.png" />
        </Btn>
        <BackHome>Back Home</BackHome>
      </BackHomeWrapper>
      <MovieCard>
        <MovieDetails>
          <H2>{movie.Title}</H2>
          <Rating imdbRating={movie.imdbRating} />
          <Views>10M+ views</Views>
          <IMDb src="/images/IMDB.png" />
          <Description>{movie.Plot}</Description>
        </MovieDetails>
        <Image src={movie.Poster} />
      </MovieCard>
    </Div>
  );
};

export default SelectedMovie;
