import React from "react";
import styled from "styled-components";
import StarIcon from "@mui/icons-material/Star"; // Полная звезда
import StarBorderRoundedIcon from "@mui/icons-material/StarBorderRounded";
import StarHalfRoundedIcon from "@mui/icons-material/StarHalfRounded";
import { MoreInfo } from "../../buttons/WatchNow";
import Plus from "../../buttons/Plus";

const Wrapper = styled.div`
  margin-top: 60px;
  margin-left: 227px;
`;

const MainTitle = styled.h2`
  font-family: Lato;
  font-size: 30px;
  font-weight: 800;
  line-height: 30px;
`;

const Card = styled.div`
  width: 237px;
  height: 298px;
  margin-top: 30px;
  position: relative;
`;

const Title = styled.h3`
  font-family: Lato;
  font-size: 24px;
  font-weight: 800;
  line-height: 24px;
  position: absolute;
  top: 21px;
  left: 21px;
`;

const EpisodeGenreWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  margin: 0px 10px;
`;

const Episodes = styled.p``;

const Genre = styled.p``;

const Stars = styled.p`
  position: absolute;
`;

const Image = styled.img`
  width: 100%;
`;

const Movies = () => {
  return (
    <Wrapper>
      <MainTitle>Movies</MainTitle>
      <Card>
        <Image src="/images/LokiMovies.png" />
        <Title>Loki</Title>
        <Stars>
          <StarIcon sx={{ color: "yellow", fontSize: "15px" }} />
          <StarIcon sx={{ color: "yellow", fontSize: "15px" }} />
          <StarIcon sx={{ color: "yellow", fontSize: "15px" }} />
          <StarIcon sx={{ color: "yellow", fontSize: "15px" }} />
          <StarHalfRoundedIcon sx={{ color: "yellow", fontSize: "15px" }} />
          <EpisodeGenreWrapper>
            <Episodes>6 Ep</Episodes>
            <Genre>Superhero</Genre>
          </EpisodeGenreWrapper>
        </Stars>
        <MoreInfo>More Info</MoreInfo>
        <Plus />
      </Card>
    </Wrapper>
  );
};

export default Movies;
