import React, { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components";
import MovieCard from "./MovieCard";
import { Link } from "react-router-dom";

const Section = styled.section`
  margin-top: 210px;
`;
const Wrapper = styled.div`
  display: flex;
  gap: 34px;
`;

const H2 = styled.h2`
  font-family: Lato;
  font-size: 48px;
  font-weight: 800;
  line-height: 48px;
  margin-bottom: 66px;
`;

const movieQuantity = 3;

const baseUrl = "https://www.omdbapi.com/";
const apiKey = "?apikey=8eac0ca3";
const Url = `${baseUrl}${apiKey}&s=movie`;

const MainTopRated = () => {
  const [movies, setMovies] = useState([]);
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(Url);
        setMovies(response.data.Search);
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, []);
  return (
    <Section>
      <H2>Top Rated Movies</H2>
      <Wrapper>
        {movies.slice(0, movieQuantity).map((movie) => (
          <Link to={`/about-movie/${movie.imdbID}`}>
            <MovieCard key={movie.imdbID} imdbID={movie.imdbID} />
          </Link>
        ))}
      </Wrapper>
    </Section>
  );
};

export default MainTopRated;
