import axios from "axios";
import React, { useEffect, useState } from "react";
import styled from "styled-components";

const SearchContainer = styled.div`
  position: relative;
`;

const SearchBar = styled.input`
  background-color: #21242d;
  width: 315px;
  height: 56px;
  border-radius: 12px;
  border: 1px solid rgba(249, 249, 249, 0.1);
  color: ${({ theme }) => (theme.isDarkMode ? "#fff" : "black")};
  background-color: ${({ theme }) => (theme.isDarkMode ? "#21242D" : "#fff")};
  filter: ${({ theme }) => (theme.isDarkMode ? "invert(0)" : "invert(1)")};
  padding-left: 64px;
`;

const SearchIcon = styled.img`
  position: absolute;
  left: 25px;
  top: 16px;
`;

const KeyIcon = styled.img`
  position: absolute;
  left: 270px;
  top: 16px;
`;

const MovieContainer = styled.div`
  position: absolute;
  top: 80px; /* или любое другое значение в зависимости от вашего дизайна */
  left: 0;
  width: 100%;
`;

const MovieList = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
  gap: 20px;
`;

const MovieItem = styled.div`
  text-align: center;
`;

const Search = ({ movieTitle }) => {
  const [results, setResults] = useState([]);

  useEffect(() => {
    const fetchByName = async () => {
      const response = await axios.get(
        `https://www.omdbapi.com/?apikey=8eac0ca3&s=${movieTitle}`
      );

      setResults(response.data.Search || []);
    };

    if (movieTitle) {
      fetchByName();
    } else {
      setResults([]);
    }
  }, [movieTitle]);

  return (
    <MovieContainer>
      <MovieList>
        {results.map((movie) => (
          <MovieItem key={movie.imdbID}>
            <img
              src={movie.Poster}
              alt={movie.Title}
              style={{ width: "100%", height: "auto" }}
            />
            <p>{movie.Title}</p>
          </MovieItem>
        ))}
      </MovieList>
    </MovieContainer>
  );
};

const NavRight = () => {
  const [searchInput, setSearchInput] = useState("");

  const handleInputChange = (e) => {
    setSearchInput(e.target.value);
  };

  return (
    <SearchContainer>
      <SearchBar
        onChange={handleInputChange}
        value={searchInput}
        placeholder="Search"
      />
      <SearchIcon src="/images/Search.png" alt="Search Icon" />
      <KeyIcon src="/images/Key.png" alt="Key Icon" />
      <Search movieTitle={searchInput} />
    </SearchContainer>
  );
};

export default NavRight;
