import React, { useEffect, useState } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import { Pagination } from "swiper/modules";
import axios from "axios";
import MovieCard from "../components/Main/MainTopRated/MovieCard";

const TopRated = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await axios.get(
          "https://www.omdbapi.com/?apikey=YOUR_API_KEY&s=movie"
        );
        if (response.data && response.data.Search) {
          setMovies(response.data.Search); // Сохраняем фильмы в состоянии
        }
      } catch (error) {
        console.error(error);
      }
    };
    fetchMovies();
  }, []);

  return (
    <Swiper
      slidesPerView={4}
      spaceBetween={30}
      pagination={{
        clickable: true,
      }}
      modules={[Pagination]}
      className="mySwiper"
    >
      {movies.map((movie) => (
        <SwiperSlide key={movie.imdbID}>
          <MovieCard movieData={movie} />{" "}
          {/* Передаем данные о фильме в MovieCard */}
        </SwiperSlide>
      ))}
    </Swiper>
  );
};

export default TopRated;
