import React from "react";
import Header from "./components/Header";
import "./App.css";
import { ThemeProvider } from "styled-components";
import { useSelector } from "react-redux";
import { createGlobalStyle } from "styled-components";

const GlobalStyle = createGlobalStyle`
  body {
    background-color: ${({ theme }) => (theme.isDarkMode ? "#00000066" : "#fff")};
    color: ${({ theme }) => (theme.isDarkMode ? "#fff" : "#00000066")};
    transition: all 0.2s ease;

  }
`;

const App = () => {
  const isDarkMode = useSelector((state) => state.theme.isDarkMode); // Правильный доступ к состоянию темы

  return (
    <ThemeProvider theme={{ isDarkMode }}>
      <GlobalStyle />
      <div className="app">
        <Header />
      </div>
    </ThemeProvider>
  );
};

export default App;
