import React from "react";
import styled from "styled-components";
import StarIcon from "@mui/icons-material/Star";
import StarBorderRoundedIcon from "@mui/icons-material/StarBorderRounded";
import StarHalfRoundedIcon from "@mui/icons-material/StarHalfRounded";

const YellowStar = styled(StarIcon)`
  color: orange;
`;

const YellowStarHalf = styled(StarHalfRoundedIcon)`
  color: yellow;
`;

const YellowStarBorder = styled(StarBorderRoundedIcon)`
  color: yellow;
`;

const Rating = ({ imdbRating }) => {
  const rating = parseFloat(imdbRating) / 2; // Нормализуем рейтинг
  const fullStars = Math.floor(rating);
  const hasHalfStar = rating % 1 >= 0.5;
  const emptyStars = 5 - fullStars - (hasHalfStar ? 1 : 0);

  return (
    <div>
      {Array.from({ length: fullStars }, (_, index) => (
        <YellowStar key={`full-${index}`} />
      ))}
      {hasHalfStar && <YellowStarHalf />}
      {Array.from({ length: emptyStars }, (_, index) => (
        <YellowStarBorder key={`empty-${index}`} />
      ))}
    </div>
  );
};

export default Rating;
