import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";

const NavList = styled.nav`
  display: flex;
  align-items: center;
`;

const Ul = styled.ul`
  display: flex;
  gap: 50px;
`;

const ListItem = styled.li`
  list-style: none;
`;

const StyledLink = styled(Link)`
  font-family: "Lato", sans-serif;
  font-weight: 600;
  font-size: 24px;
  line-height: 24px;
  text-decoration: none;
  color: ${({ theme }) => (theme.isDarkMode ? "#fff" : "black")};
`;

const NavBar = styled.div`
  display: flex;
  gap: 100px;
`;

const SearchBar = styled.input`
  background-color: ${({ theme }) => (theme.isDarkMode ? "#21242D" : "#fff")};
  width: 100%;
  height: 100%;
  border-radius: 12px;
  border: 1px solid rgba(249, 249, 249, 0.1);
  color: ${({ theme }) => (theme.isDarkMode ? "#fff" : "black")};
  padding-left: 40px; /* Чтобы текст не перекрывался с иконкой */
`;

const SearchIcon = styled.img`
  position: absolute;
  left: 10px;
`;

const NavRight = () => {
  return (
    <NavBar>
      <NavList>
        <Ul>
          <ListItem>
            <StyledLink style={{ color: "#F8B319" }} to="/movies">
              Movies
            </StyledLink>
          </ListItem>
          <ListItem>
            <StyledLink to="/tv-shows">TV Shows</StyledLink>
          </ListItem>
          <ListItem>
            <StyledLink to="/anime">Anime</StyledLink>
          </ListItem>
        </Ul>
      </NavList>
      <SearchBar placeholder="Search" />
      <SearchIcon src="/images/Search.png" />
    </NavBar>
  );
};

export default NavRight;
