import React from "react";
import DarkMode from "../DarkMode";
import styled, { ThemeProvider } from "styled-components";
import { Link } from "react-router-dom";
import { useSelector } from "react-redux";

const AsideBar = styled.aside`
  position: relative;
  left: -510px;
`;

const Nav = styled.nav``;

const Ul = styled.ul`
  list-style: none;
  margin-top: 59px;
  display: flex;
  flex-direction: column;
  gap: 25px;
`;

const FirstUl = styled(Ul)`
  margin-top: 39px;
`;

const ListTitle = styled.p`
  font-family: Lato;
  font-weight: 700;
  font-size: 18px;
  line-height: 21.6px;
  color: ${({ theme }) =>
    theme.isDarkMode ? "rgba(249, 249, 249, 0.67)" : "black"};
`;

const Li = styled.li`
  color: ${({ theme }) => (theme.isDarkMode ? "#fff" : "black")};
  margin-top: 15px;
  display: flex;
  gap: 15px;
`;

const StyledLink = styled(Link)`
  background-color: ${({ theme }) => (theme.isDarkMode ? "black" : "#fff")};
  color: ${({ theme }) => (theme.isDarkMode ? "#fff" : "black")};
  text-decoration: none;
  font-family: Lato, sans-serif;
  font-size: 18px;
  font-weight: 600;
  line-height: 21.6px;
  text-align: left;
  display: flex;
  gap: 15px;
`;

const Image = styled.img`
  filter: ${({ theme }) =>
    theme.isDarkMode ? "invert(0)" : "invert(1)"}; /* Инвертируем цвет иконки */
`;

const Aside = () => {
  const isDarkMode = useSelector((state) => state.theme.isDarkMode);

  return (
    <ThemeProvider theme={{ isDarkMode }}>
      <AsideBar>
        <Nav>
          <FirstUl>
            <ListTitle>
              Menu
              <Li>
                <div>
                  {isDarkMode ? "Light Mode" : "Dark Mode"}
                  <DarkMode />
                </div>
              </Li>
            </ListTitle>
            <Li>
              <StyledLink to="/">
                <Image src="/images/Squircle.png" alt="Home" />
                Home
              </StyledLink>
            </Li>
            <Li>
              <StyledLink to="/">
                <Image src="/images/Discover.png" alt="Discover" />
                Discover
              </StyledLink>
            </Li>
            <Li>
              <StyledLink to="/">
                <Image src="/images/Award.png" alt="Awards" />
                Awards
              </StyledLink>
            </Li>
            <Li>
              <StyledLink to="/">
                <Image src="/images/Celebrities.png" alt="Celebrities" />
                Celebrities
              </StyledLink>
            </Li>
          </FirstUl>
          {/* Остальные списки */}
        </Nav>
      </AsideBar>
    </ThemeProvider>
  );
};

export default Aside;
